package loopingex;

public class Nestedforloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++) {

			System.out.println("Parent i value is: " + i);
			// nested for loop
			for (int j = 0; j < 5; j++) {

				System.out.println("Child j value is: " + j);
	}

}
		for(int i=0; i<5;i++ ) {
			for(int j=0;j<5;j++ ) {
			System.out.print(" * ");
			System.out.print(" * ");
	}
			System.out.println();
	}
}
}
